import pygame# ------------Добавили класс противника-----------------#class Enemy(pygame.sprite.Sprite):    def __init__(self, x, y):        pygame.sprite.Sprite.__init__(self)        self.image = pygame.image.load('img\\sprite_01.png')        self.rect = self.image.get_rect()        self.rect.x = x        self.rect.y = y        self.move_direction = 1        self.move_counter = 0        self.flip = False        self.counter = 0        self.index = 0        self.walk_cooldown = 8        self.HP = 3        self.anim_listL = []        self.anim_listR = [pygame.image.load('img\\sprite_01.png'), pygame.image.load('img\\sprite_02.png')]        for i in self.anim_listR:            img = pygame.transform.flip(i, True, False)            self.anim_listL.append(img)    def update(self):        # --------делаем патриулируйщего противника--------#        self.rect.x += self.move_direction        self.move_counter += 1        if abs(self.move_counter) > 50:            self.move_direction *= -1            self.move_counter *= -1        if self.move_direction > 0:            if self.counter > self.walk_cooldown:                if self.index > 1:                    self.index = 0                self.counter = 0                self.image = self.anim_listR[self.index]                self.index += 1            self.counter += 1        else:            if self.counter > self.walk_cooldown:                if self.index > 1:                    self.index = 0                self.counter = 0                self.image = self.anim_listL[self.index]                self.index += 1            self.counter += 1