import pygamefrom src.EnemyClass import Enemyfrom src.ExitClass import Exitfrom src.config_screen import screen_from src.InteractionObjectClass import InteractionObjectfrom src.bulletClass import Bulletcut_group = pygame.sprite.Group()fire_group = pygame.sprite.Group()exit_group = pygame.sprite.Group()blob_group = pygame.sprite.Group()key_group = pygame.sprite.Group()bullet_group = pygame.sprite.Group()screen = screen_()tile_size = 16class World:    """    Класс для представления игрового мира.    """    def __init__(self, data):        """        Инициализация мира на основе данных.        Параметры:        data (list): Двумерный список, представляющий уровень игры.        """        self.tile_list = []  # Список тайлов в мире        self.index = 0        self.counter = 0        self.data = data        # Загрузка изображений тайлов        platform_img = pygame.image.load('img\\PlatfomE.png')        cut_img = pygame.image.load('img\\cut.png')        # Обход данных уровня и создание тайлов        row_count = 0        for row in data:            col_count = 0            for tile in row:                if tile == 1:                    # Создание тайла земли                    img = pygame.transform.scale(platform_img, (tile_size, tile_size))                    img_rect = img.get_rect()                    img_rect.x = col_count * tile_size                    img_rect.y = row_count * tile_size                    tile = (img, img_rect)                    self.tile_list.append(tile)                if tile == 2:                    coin = InteractionObject(col_count * tile_size + (tile_size // 2),                                             row_count * tile_size + (tile_size // 2), "img\\cut.png")                    cut_group.add(coin)                if tile == 3:                    blob = Enemy(col_count * tile_size,                                 row_count * tile_size + 15)                    blob_group.add(blob)                if tile == 9:                    fire = InteractionObject(col_count * tile_size + (tile_size // 2),                                             row_count * tile_size + (tile_size // 2), "img\\fakel.png")                    fire_group.add(fire)                if tile == 8:                    exit = Exit(col_count * tile_size, row_count *                                tile_size - (tile_size // 2))                    exit_group.add(exit)                if tile == 4:                    keys = InteractionObject(col_count * tile_size + (tile_size // 2),                                             row_count * tile_size + (tile_size // 2), "img\\key\\sprite_0.png")                    key_group.add(keys)                if tile == 6:                    bullet = Bullet(col_count * tile_size + (tile_size // 2),                                    row_count * tile_size + (tile_size // 2))                    bullet_group.add(bullet)                col_count += 1            row_count += 1    def draw(self):        """        Отрисовка всех тайлов мира на экране.        """        for tile in self.tile_list:            screen.blit(tile[0], tile[1])