import pygameclass Bullet(pygame.sprite.Sprite):    def __init__(self, x, y):        pygame.sprite.Sprite.__init__(self)        self.image = pygame.image.load('img\\bullet\\bullet.png')        self.rect = self.image.get_rect()        self.rect.x = x        self.rect.y = y        self.move_direction = -5        self.move_counter = 0    def update(self):        # --------делаем патриулируйщего противника--------#        self.rect.x += self.move_direction